    function post_date(feed, i) {
        var c = feed[i].published.$t,
            d = c.substring(0, 4),
            f = c.substring(5, 7),
            m = c.substring(8, 10),
            h = monthFormat[parseInt(f, 10) - 1] + ' ' + m + ', ' + d,
            code = '<span class="post-date">' + h + '</span>';
        return code;
    }

  function postThumb($c, img) {
	var $h = $('<div>').html($c),
		$t = $h.find('img:first').attr('src'),
		$a = $t.lastIndexOf('/') || 0,
		$b = $t.lastIndexOf('/', $a - 1) || 0,
		$p0 = $t.substring(0, $b),
		$p1 = $t.substring($b, $a),
		$p2 = $t.substring($a);
	if($p1.match(/\/s[0-9]+/g) || $p1.match(/\/w[0-9]+/g) || $p1 == '/d') {
		$p1 = '/w72-h72-p-k-no-nu'
	}
	img = $p0 + $p1 + $p2;
	return img
}

function FeatImage(feed, i) {
	var $c = feed[i].content.$t;
	if(feed[i].media$thumbnail) {
		var src = feed[i].media$thumbnail.url
	} else {
		src = noThumbnail;
	}
	if($c.indexOf($c.match(/<iframe(?:.+)?src=(?:.+)?(?:www.youtube.com)/g)) > -1) {
		if($c.indexOf('<img') > -1) {
			if($c.indexOf($c.match(/<iframe(?:.+)?src=(?:.+)?(?:www.youtube.com)/g)) < $c.indexOf('<img')) {
				img = src.replace('/default.', '/0.')
			} else {
				img = postThumb($c)
			}
		} else {
			img = src.replace('/default.', '/0.')
		}
	} else if($c.indexOf('<img') > -1) {
		img = postThumb($c)
	} else {
		img = noThumbnail;
	}
     var code = '<img class="post-thumb" alt="" src="' + img + '"/>';
        return code;
}

    function post_label(feed, i) {
        if (feed[i].category != undefined) {
            var tag = feed[i].category[0].term,
                code = '<span class="post-tag">' + tag + '</span>';
        } else {
            code = '';
        }
        return code;
    }

    function ajaxPosts($this, type, num, label) {
        if (type.match('mega-menu') || type.match('slider') || type.match('post-list') || type.match('related')) {
            var url = '';
            if (label == 'recent') {
                url = '/feeds/posts/default?alt=json-in-script&max-results=' + num;
            } else if (label == 'random') {
                var index = Math.floor(Math.random() * num) + 1;
                url = '/feeds/posts/default?max-results=' + num + '&start-index=' + index + '&alt=json-in-script';
            } else {
                url = '/feeds/posts/default/-/' + label + '?alt=json-in-script&max-results=' + num;
            }
            $.ajax({
                url: url,
                type: 'get',
                dataType: 'jsonp',
                beforeSend: function() {
                      if (type.match('slider')) {
                        $this.html('<div class="loader"></div>').parent().addClass('show-slider');
                    }
                },


                success: function(json) {
                    if (type.match('mega-menu')) {
                        var kode = '<ul class="mega-menu-inner">';
                    } else if (type.match('slider')) {
                        var kode = '<ul class="main-slider">';
                    } else if (type.match('post-list')) {
                        var kode = '<ul class="custom-widget">';
                    } else if (type.match('related')) {
                        var kode = '<ul class="related-posts">';
                    }
                    var entry = json.feed.entry;
                    if (entry != undefined) {
                        for (var i = 0, feed = entry; i < feed.length; i++) {
                            var link = post_link(feed, i),
                                title = post_title(feed, i, link),
                                image = FeatImage(feed, i, link),
                                tag = post_label(feed, i),
                                author = post_author(feed, i),
                                date = post_date(feed, i),
                                snip = post_snip(feed, i);
                            var kontent = '';
                            if (type.match('mega-menu')) {
                                kontent += '<div class="mega-item item-' + i + '"><div class="mega-content"><div class="post-image-wrap"><a class="post-image-link" href="' + link + '">' + image + '</a></div><h2 class="post-title">' + title + '</h2><div class="post-meta">' + date + '</div></div></div>';
                                } else if (type.match('slider')) {
                                kontent += '<li class="slider-item item-' + (i + 1) + '"><div class="slider-item-inner"><a class="post-image-link" href="' + link + '">' + image + '</a><div class="post-info-wrap"><div class="post-info"><h2 class="post-title">' + title + '</h2>' + snip + '<a class="ak" href=' + link + '><p>Learn More</p></a></div></div></li>';
                                }  else if (type.match('post-list')) {
                                kontent += '<li class="item-' + i + '"><a class="post-image-link" href="' + link + '">' + image + '</a><div class="post-info"><h2 class="post-title">' + title + '</h2><div class="post-meta">' + date + '</div></div></div></li>';
                            } else if (type.match('related')) {
                                kontent += '<li class="related-item item-' + i + '"><div class="post-image-wrap"><a class="post-image-link" href="' + link + '">' + image + '</a>' + tag + '</div><h2 class="post-title">' + title + '</h2><div class="post-meta">' + date + '</div></li>';
                            }
                            kode += kontent;
                        }
                        kode += '</ul>';
                    } else {
                        kode = '<ul class="no-posts">Error: No Posts Found <i class="fa fa-frown"/></ul>';
                    }
                    if (type.match("mega-menu")) {
                        $this.addClass('has-sub mega-menu').append(kode);
                        $this.find('a:first').attr('href', function($this, href) {
                            if (label == 'recent' || label == 'random') {
                                href = href.replace(href, '/search/?&max-results=' + postPerPage);
                            } else {
                                href = href.replace(href, '/search/label/' + label + '?&max-results=' + postPerPage);
                            }
                            return href;
                        });

                        } else if (type.match('slider')) {
                        $this.html(kode).parent().addClass('show-slider');
                       $('.main-slider').slick({
                            dots: true,
                            infinite: true,
                            speed: 600,
                            autoplay: true,
                            autoplaySpeed: 4000,
                            slidesToShow: 1,
                            centerPadding: 0,
                            prevArrow: '<div class="slick-prev"/>',
                            nextArrow: '<div class="slick-next"/>',
                            cssEase: 'linear',
                            responsive: [{
                                breakpoint: 981,
                                settings: {
                                    slidesToShow: 1
                                }
                            }, {
                                breakpoint: 681,
                                settings: {
                                    slidesToShow: 1
                                }
                            }]
                        });
                    } else if (type.match('hot-posts')) {
                        $this.html(kode).parent().addClass('show-hot');
                    } else {
                        $this.html(kode);
                    }
$this.find('.post-thumb').lazyyard();
                }
            });
        }
    }
    $('.blog-post-comments').each(function() {
        var system = commentsSystem,
            disqus_url = disqus_blogger_current_url,
            disqus = '<div id="disqus_thread"/>',
            current_url = $(location).attr('href'),
            facebook = '<div class="fb-comments" data-width="100%" data-href="' + current_url + '" data-numposts="5"></div>',
            sClass = 'comments-system-' + system;
        if (system == 'blogger') {
            $(this).addClass(sClass).show();
        } else if (system == 'disqus') {
            (function() {
                var dsq = document.createElement('script');
                dsq.type = 'text/javascript';
                dsq.async = true;
                dsq.src = '//' + disqusShortname + '.disqus.com/embed.js';
                (document.getElementsByTagName('head')[0] || document.getElementsByTagName('body')[0]).appendChild(dsq);
            })();
            $('#comments, #gpluscomments').remove();
            $(this).append(disqus).addClass(sClass).show();
        } else if (system == 'facebook') {
            $('#comments, #gpluscomments').remove();
            $(this).append(facebook).addClass(sClass).show();
        } else if (system == 'hide') {
            $(this).hide();
        } else {
            $(this).addClass('comments-system-default').show();
        }
    });
});
